import React, { useState } from 'react';
import QueryInput from './components/QueryInput';
import ResponseDisplay from './components\ResponseDisplay';
import { FrontendResponse } from './types/response';
import apiClient from './services/api-client';

const App: React.FC = () => {
  const [response, setResponse] = useState<FrontendResponse | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [sessionId, setSessionId] = useState<string | undefined>(undefined);

  const handleQuerySubmit = (response: FrontendResponse) => {
    setResponse(response);
    setIsLoading(false);
  };

  const handleSessionIdChange = (newSessionId: string) => {
    setSessionId(newSessionId);
  };

  // Initialize a session when the app loads
  React.useEffect(() => {
    const initializeSession = async () => {
      try {
        const session = await apiClient.createSession();
        setSessionId(session.session_id);
      } catch (error) {
        console.error('Failed to initialize session:', error);
        // Create a temporary session ID if API fails
        setSessionId(`temp-session-${Date.now()}`);
      }
    };

    initializeSession();
  }, []);

  return (
    <div className="app">
      <header className="app-header">
        <h1>Backend-Frontend Integration</h1>
        <p>Query the AI agent with retrieval-augmented generation</p>
      </header>

      <main className="app-main">
        <QueryInput
          onQuerySubmit={handleQuerySubmit}
          sessionId={sessionId}
          onSessionIdChange={handleSessionIdChange}
        />

        <ResponseDisplay
          response={response}
          isLoading={isLoading}
        />
      </main>

      <footer className="app-footer">
        <p>Powered by FastAPI, React, and AI Retrieval Technology</p>
      </footer>
    </div>
  );
};

export default App;
